import React, { useState } from "react";
import axios from "axios";

const UploadFiles = () => {
  const [images, setImages] = useState([]);
  const [variants, setVariants] = useState([]);
  const [uploadStatus, setUploadStatus] = useState("");
  const [uploadedData, setUploadedData] = useState(null);

  // Handle image selection
  const handleImageChange = (event) => {
    setImages(event.target.files);
  };

  // Handle variant selection
  const handleVariantChange = (event) => {
    setVariants(event.target.files);
  };

  // Handle upload
  const handleUpload = async () => {
    if (images.length === 0 || variants.length === 0) {
      setUploadStatus("Please select both images and variants.");
      return;
    }

    const formData = new FormData();

    // Append images
    Array.from(images).forEach((file) => formData.append("images", file));

    // Append variants
    Array.from(variants).forEach((file) => formData.append("images-variants", file));

    try {
      setUploadStatus("Uploading...");
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setUploadedData(response.data); // Store the response data (unique ID and file URLs)
      setUploadStatus("Upload successful!");
    } catch (error) {
      console.error("Error uploading files:", error);
      setUploadStatus("Error uploading files.");
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h2>Upload Images and Variants</h2>
      <div>
        <label>Images:</label>
        <input type="file" multiple onChange={handleImageChange} />
      </div>
      <div>
        <label>Variants:</label>
        <input type="file" multiple onChange={handleVariantChange} />
      </div>
      <button onClick={handleUpload} style={{ marginTop: "10px" }}>
        Upload
      </button>
      {uploadStatus && <p>{uploadStatus}</p>}
      {uploadedData && (
        <div>
          <h3>Uploaded Data:</h3>
          <p>Unique ID: {uploadedData.id}</p>
          <h4>Images:</h4>
          <ul>
            {uploadedData.images.map((url, index) => (
              <li key={index}>
                <a href={url} target="_blank" rel="noopener noreferrer">
                  {url}
                </a>
              </li>
            ))}
          </ul>
          <h4>Variants:</h4>
          <ul>
            {uploadedData.variants.map((url, index) => (
              <li key={index}>
                <a href={url} target="_blank" rel="noopener noreferrer">
                  {url}
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default UploadFiles;
