import React, { useState } from "react";
import axios from "axios";
import './uploadFolder.css';


const UploadFiles = () => {
  const [selectedFile, setSelectedFile] = useState(null); // File selected by the user
  const [uploadStatus, setUploadStatus] = useState("");  // Status message
  const [uploadedFileUrl, setUploadedFileUrl] = useState(""); // URL of uploaded file

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Handle file upload
  const handleFileUpload = async () => {
    if (!selectedFile) {
      setUploadStatus("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile); // Add the file to FormData
    formData.append("folderType", "original"); // Add folder type (e.g., 'original' or 'variant')

    try {
      setUploadStatus("Uploading...");
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setUploadStatus("File uploaded successfully!");
      setUploadedFileUrl(response.data.fileUrl); // Get uploaded file URL
    } catch (error) {
      console.error("Error uploading file:", error);
      setUploadStatus("Error uploading file.");
    }
  };

  return (
    <div style={{ margin: "20px" }}>
      <h2>Upload File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload} style={{ marginLeft: "10px" }}>
        Upload
      </button>
      {uploadStatus && <p>{uploadStatus}</p>}
      {uploadedFileUrl && (
        <div>
          <h3>Uploaded File:</h3>
          <a href={uploadedFileUrl} target="_blank" rel="noopener noreferrer">
            {uploadedFileUrl}
          </a>
        </div>
      )}
    </div>
  );
};

export default UploadFiles;
