import React, { useState } from "react";
import axios from "axios";

const RemoveBackground = () => {
  const [folderId, setFolderId] = useState("");
  const [itemName, setItemName] = useState("");
  const [image, setImage] = useState(null);
  const [response, setResponse] = useState(null);
  const [error, setError] = useState("");

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!folderId || !itemName || !image) {
      setError("Please fill in all fields and upload an image.");
      return;
    }

    setError(""); // Clear previous errors
    const formData = new FormData();
    formData.append("folderId", folderId);
    formData.append("itemName", itemName);
    formData.append("image", image);

    try {
      const res = await axios.post("http://localhost:6500/remove-background", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setResponse(res.data); // Set the server response
    } catch (err) {
      setError(err.response?.data?.error || "Something went wrong.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "400px", margin: "0 auto" }}>
      <h2>Remove Background</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Folder ID:
            <input
              type="text"
              value={folderId}
              onChange={(e) => setFolderId(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Item Name:
            <input
              type="text"
              value={itemName}
              onChange={(e) => setItemName(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Image:
            <input type="file" accept="image/*" onChange={handleImageChange} required />
          </label>
        </div>
        <button type="submit">Submit</button>
      </form>
      {error && <p style={{ color: "red" }}>{error}</p>}
      {response && (
        <div>
          <h3>Response:</h3>
          <p>Folder ID: {response.folderId}</p>
          <p>Item Name: {response.itemName}</p>
          <p>
            Original Image:{" "}
            <a href={`http://localhost:6500${response.originalImage}`} target="_blank" rel="noopener noreferrer">
              View
            </a>
          </p>
          <p>
            Background Removed Image:{" "}
            <a href={`http://localhost:6500${response.bgRemovedImage}`} target="_blank" rel="noopener noreferrer">
              View
            </a>
          </p>
        </div>
      )}
    </div>
  );
};

export default RemoveBackground;
