import React from "react";
import axios from "axios";

const UploadFiles = () => {
  const handleFolderUpload = async (event) => {
    const files = event.target.files;
    const formData = new FormData();

    for (let file of files) {
      // Assign files to their respective categories based on folder structure
      if (file.webkitRelativePath.includes("images/")) {
        formData.append("images", file); // Add to images
      } else if (file.webkitRelativePath.includes("images-variants/")) {
        formData.append("images-variants", file); // Add to variants
      }
    }

    try {
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("Upload successful:", response.data);
    } catch (error) {
      console.error("Error uploading files:", error);
    }
  };

  return (
    <div>
      <h2>Upload Folders</h2>
      <input type="file" webkitdirectory="true" directory="true" multiple onChange={handleFolderUpload} />
    </div>
  );
};

export default UploadFiles;
