import axios from "axios";

// Function to fetch item information by folderId
const getItemInfo = async (folderId) => {
  try {
    const response = await axios.get(`http://localhost:3008/info/${folderId}`);
    return response.data; // Return the folder data
  } catch (error) {
    console.error("Error fetching folder information:", error);
    throw error; // Rethrow the error for handling in the caller
  }
};

export default getItemInfo;
