import React, { useState } from "react";

const AddTheme = () => {
  const [themeData, setThemeData] = useState({
    theme: "",
    image: "",
    mode: "default",
    types: ""
  });

  const [message, setMessage] = useState("");

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setThemeData({
      ...themeData,
      [name]: value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3001/themes", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          ...themeData,
          types: themeData.types.split(",") // Convert types from string to array
        })
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(`Theme added successfully: ${data.theme.theme}`);
        setThemeData({ theme: "", image: "", mode: "default", types: "" });
      } else {
        const errorData = await response.json();
        setMessage(`Error: ${errorData.message}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  return (
    <div sty>
      <h2>Add a New Theme</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Theme:
          <input
            type="text"
            name="theme"
            value={themeData.theme}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Image URL:
          <input
            type="text"
            name="image"
            value={themeData.image}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Mode:
          <select
            name="mode"
            value={themeData.mode}
            onChange={handleChange}
          >
            <option value="default">Default</option>
            <option value="full">Full</option>
          </select>
        </label>
        <br />
        <label>
          Types (comma-separated):
          <input
            type="text"
            name="types"
            value={themeData.types}
            onChange={handleChange}
            placeholder="Type1,Type2,Type3"
          />
        </label>
        <br />
        <button type="submit">Add Theme</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddTheme;
