import React, { useState } from "react";
import axios from "axios";

function UploadFolder() {
  const [folderId, setFolderId] = useState(Date.now().toString()); // Unique folder ID
  const [files, setFiles] = useState([]);
  const [video, setVideo] = useState(null);
  const [textFiles, setTextFiles] = useState([{ name: "", content: "" }]); // For multiple text files

  const handleFileChange = (e) => {
    setFiles(e.target.files);
  };

  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const handleTextChange = (index, field, value) => {
    const updatedTextFiles = [...textFiles];
    updatedTextFiles[index][field] = value;
    setTextFiles(updatedTextFiles);
  };

  const addTextFile = () => {
    setTextFiles([...textFiles, { name: "", content: "" }]);
  };

  const uploadFiles = async () => {
    const formData = new FormData();

    // Add folder ID to formData
    formData.append("folderId", folderId);

    // Categorize files into images and images-variants
    Array.from(files).forEach((file) => {
      console.log(file.webkitRelativePath); // Log to see the directory structure

      // Check if the file is in the 'images' folder
      if (file.webkitRelativePath.includes("images/")) {
        formData.append("images", file);
      }
      // Check if the file is in the 'images-variants' folder
      else if (file.webkitRelativePath.includes("images-variants/")) {
        formData.append("images-variants", file);
      }
    });

    // Add video file
    if (video) {
      formData.append("video", video);
    }

    // Add text files
    if (textFiles.length > 0) {
      formData.append("textFiles", JSON.stringify(textFiles));
    }

    try {
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("Upload successful:", response.data);
    } catch (error) {
      console.error("Error uploading files:", error);
    }
  };

  return (
    <div>
      <h2>Upload Folder</h2>

      {/* Folder Input */}
      <label>Upload Images & Images-Variants:</label>
      <input type="file" webkitdirectory="true" multiple onChange={handleFileChange} />
      <br />

      {/* Video Input */}
      <label>Upload Video:</label>
      <input type="file" accept="video/*" onChange={handleVideoChange} />
      <br />

      {/* Text Input */}
      <label>Add Text Files:</label>
      {textFiles.map((textFile, index) => (
        <div key={index}>
          <input
            type="text"
            placeholder="Text File Name"
            value={textFile.name}
            onChange={(e) => handleTextChange(index, "name", e.target.value)}
          />
          <textarea
            placeholder="Text Content"
            value={textFile.content}
            onChange={(e) => handleTextChange(index, "content", e.target.value)}
          />
        </div>
      ))}
      <button onClick={addTextFile}>Add Another Text File</button>
      <br />

      {/* Submit Button */}
      <button onClick={uploadFiles}>Upload</button>
    </div>
  );
}

export default UploadFolder;
