import React, { useState } from 'react';
import axios from 'axios';

const AddItemForm = () => {
  const [category, setCategory] = useState('');
  const [name, setName] = useState('');
  const [usdPrice, setUsdPrice] = useState('');
  const [cryptocurrency, setCryptocurrency] = useState('BNB');
  const [network, setNetwork] = useState('ethereum');
  const [numberOfItems, setNumberOfItems] = useState('');
  const [text, setText] = useState('');
  const [imageUrl, setImageUrl] = useState(''); // Manually set the image URL here

  const imageUr = "https://firebasestorage.googleapis.com/v0/b/benege-93e7c.appspot.com/o/uploads%2Fbngr.png";

  const handleSubmit = async (e) => {
    e.preventDefault();

    const itemData = {
      category,
      item: {
        name,
        usdPrice,
        cryptocurrency,
        network,
        numberOfItems,
        images: [imageUrl], // Use the manually added URL
        text,
      },
    };

    try {
      await axios.post('http://localhost:3001/add-item', itemData);
      alert('Item added successfully!');
    } catch (error) {
      console.error('Error adding item:', error);
      alert('Failed to add item.');
    }
  };

  return (
    <>
    <div>
       <img src={imageUr} alt="Uploaded Image" width="300" height="300" />
    </div>
    <form onSubmit={handleSubmit}>
      {/* Other input fields */}
      
      <div>
        <label>Image URL:</label>
        <input
          type="text"
          placeholder="Paste image URL here"
          value={imageUrl}
          onChange={(e) => setImageUrl(e.target.value)} // Update the image URL manually
        />
      </div>

      <button type="submit">Add Item</button>
    </form>
    </>
  );
};

export default AddItemForm;
