import React, { useState, useEffect } from "react";
import { auth } from "./components/firebaseConfig";
import { signInWithEmailAndPassword } from "firebase/auth";
import FileUpload from "./components/FileUpload";
import UploadFolder from "./components/uploadFolder";
import MissingVariantsChecker from "./components/missingVariants";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";  // Import React Router
import Header from "./components/header";
import "./App.css"; // Import the CSS file
import AddTheme from "./components/AddThemes";
import AddTypeForm from "./components/addTypes";
import AddCategoryForm from "./components/AddCategoryForm";
import ModifyData from "./components/modifyData";
import ItemInfo from "./components/itemInfo";
import GetItemInfo from "./components/getItemInfo";
import RemoveSizes from "./components/removeSize";
import UploadItem from "./components/getItemId";
import BeautyHeader from "./components/beautyheader";
import WomenHeader from "./components/womenheader";
import ZoomSettings from "./components/zoom";
import ItemDetail from "./components/getItemById";
import ShoesHeader from "./components/shoesheader";
import UpdateThemeImage from "./components/updateThemeImage";
import MenHeader from "./components/menheader";
import PetHeader from "./components/petheader";
import KidsHeader from "./components/kidsheader";
import ElectronicHeader from "./components/electronicheader";
import ProductHeaderImage from "./components/productHeaderImage";
import ThemeAndInfo from "./components/themeAndInfo";
import UploadZone from "./components/uploadZone";

const App = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);

  // Scroll to top when the component is rendered or user state changes
  useEffect(() => {
    window.scrollTo(0, 0); // Scroll to the top of the page
  }, [user]); // This will trigger the scroll when the user state is updated

  const handleLogin = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        setUser(userCredential.user);
      })
      .catch((error) => {
        console.error("Login Error: ", error);
        alert("Login failed. Please try again.");
      });
  };

   // Create the navigate function using the useNavigate hook
   const navigate = useNavigate();

   const handleNavigateToBeauty = () => {
     // Navigates to beauty page with selected type
     navigate(`/beauty`);
   };

  return user ?(
    <>

    <Router>
   
    <Routes>
    <Route path="/typeimage" element={<ProductHeaderImage  />} />
    <Route path="/themeandinfo" element={<ThemeAndInfo  />} />
    <Route path="/uploadZone" element={<UploadZone />} />
    </Routes>
    
   </Router>
    </>
  ) : (
    <div className="log">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="inptim"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="inptin"
      />
      <button className="bttn" onClick={handleLogin}>Log in</button>
    </div>
    
    
  );
};

export default App;
