// src/firebaseConfig.js
import { initializeApp } from "firebase/app";
import { getStorage } from "firebase/storage";
import { getAuth } from "firebase/auth"; // Import the authentication module

// Your Firebase config
const firebaseConfig = {
    apiKey: "AIzaSyAXS9Hv7d1LVkYmB0y3sXENm28dxWtQA9k",
    authDomain: "binege-6ce8b.firebaseapp.com",
    projectId: "binege-6ce8b",
    storageBucket: "binege-6ce8b.firebasestorage.app",
    messagingSenderId: "996552723241",
    appId: "1:996552723241:web:e56af75687b5d0e2858538"
  };

// Initialize Firebase
const app = initializeApp(firebaseConfig);

// Export storage and auth
export const storage = getStorage(app);
export const auth = getAuth(app);
