import React, { useState } from "react";
import axios from "axios";

function UploadFolder() {
  const [folderId, setFolderId] = useState(Date.now().toString()); // Unique folder ID
  const [imagesFolder, setImagesFolder] = useState([]);
  const [imagesVariantsFolder, setImagesVariantsFolder] = useState([]);
  const [video, setVideo] = useState(null);
  const [textFiles, setTextFiles] = useState([]);

  // Handle images folder selection
  const handleImagesFolderChange = (e) => {
    const files = Array.from(e.target.files);
    setImagesFolder(files);
  };

  // Handle images-variants folder selection
  const handleImagesVariantsFolderChange = (e) => {
    const files = Array.from(e.target.files);

    // Group files by folder (parent directory name)
    const groupedVariants = files.reduce((acc, file) => {
      const variantPathParts = file.webkitRelativePath.split("/");
      const folderName = variantPathParts[variantPathParts.length - 2]; // Get parent folder name

      if (!acc[folderName]) acc[folderName] = [];
      acc[folderName].push(file);
      return acc;
    }, {});

    setImagesVariantsFolder(groupedVariants);
  };

  // Handle video selection
  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  // Handle text creation
  const handleAddTextFile = (textName, textContent) => {
    setTextFiles((prev) => [
      ...prev,
      { name: textName, content: textContent },
    ]);
  };

  const uploadFiles = async () => {
    const formData = new FormData();

    // Add folder ID
    formData.append("folderId", folderId);

    // Append images folder files
    imagesFolder.forEach((file) => {
      formData.append("images", file);
    });

    // Append images-variants grouped by color folders
    Object.entries(imagesVariantsFolder).forEach(([folderName, files]) => {
      files.forEach((file) => {
        formData.append(`images-variants[${folderName}]`, file);
      });
    });

    // Append video
    if (video) {
      formData.append("video", video);
    }

    // Append text files
    textFiles.forEach((textFile) => {
      formData.append(`text[${textFile.name}]`, textFile.content);
    });

    try {
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("Upload successful:", response.data);
    } catch (error) {
      console.error("Error uploading files:", error);
    }
  };

  return (
    <div>
      <h2>Upload Folder</h2>

      {/* Select Images Folder */}
      <div>
        <label>Select Images Folder:</label>
        <input type="file" webkitdirectory="true" multiple onChange={handleImagesFolderChange} />
      </div>

      {/* Select Images-Variants Folder */}
      <div>
        <label>Select Images-Variants Folder:</label>
        <input
          type="file"
          webkitdirectory="true"
          multiple
          onChange={handleImagesVariantsFolderChange}
        />
      </div>

      {/* Select Video */}
      <div>
        <label>Select Video:</label>
        <input type="file" accept="video/*" onChange={handleVideoChange} />
      </div>

      {/* Add Text Files */}
      <div>
        <h3>Add Text Content:</h3>
        <TextEditor onAdd={handleAddTextFile} />
      </div>

      {/* Upload Button */}
      <button onClick={uploadFiles}>Upload</button>
    </div>
  );
}

// TextEditor component for adding text content
function TextEditor({ onAdd }) {
  const [textName, setTextName] = useState("");
  const [textContent, setTextContent] = useState("");

  const handleAddText = () => {
    if (textName && textContent) {
      onAdd(textName, textContent);
      setTextName("");
      setTextContent("");
    }
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Text file name"
        value={textName}
        onChange={(e) => setTextName(e.target.value)}
      />
      <textarea
        placeholder="Text content"
        value={textContent}
        onChange={(e) => setTextContent(e.target.value)}
      />
      <button onClick={handleAddText}>Add Text</button>
    </div>
  );
}

export default UploadFolder;
