import React, { useState } from "react";

const UploadForm = () => {
  const [files, setFiles] = useState([]);
  const [folderID, setFolderID] = useState("");
  const [type, setType] = useState("");
  const [text, setText] = useState("");

  const handleFileChange = (e) => {
    setFiles([...e.target.files]); // Convert FileList to array
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!folderID || !type || files.length === 0) {
      alert("Please fill all fields and select at least one file.");
      return;
    }

    const formData = new FormData();
    formData.append("folderID", folderID);
    formData.append("type", type);
    formData.append("text", text);
    files.forEach((file) => formData.append("files", file)); // Append multiple files

    try {
      const response = await fetch("http://localhost:5000/api/items/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        alert("Files uploaded successfully!");
      } else {
        alert("Upload failed: " + data.error);
      }
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Upload failed.");
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Upload Files</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Folder ID"
          value={folderID}
          onChange={(e) => setFolderID(e.target.value)}
          className="border p-2 w-full"
        />
        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          className="border p-2 w-full"
        >
          <option value="">Select Type</option>
          <option value="slide_images">Slide Images</option>
          <option value="image_with_text">Image with Text</option>
          <option value="single_image">Single Image</option>
          <option value="video_with_text">Video with Text</option>
          <option value="single_video">Single Video</option>
        </select>
        <textarea
          placeholder="Text (optional)"
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="border p-2 w-full"
        ></textarea>
        <input type="file" multiple onChange={handleFileChange} className="border p-2 w-full" />
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Upload
        </button>
      </form>
    </div>
  );
};

export default UploadForm;
