import React, { useState } from "react";
import axios from "axios";

const UploadItem = () => {
  const [folderID, setFolderID] = useState("");
  const [type, setType] = useState(""); // e.g., "slides", "imageWithText", etc.
  const [text, setText] = useState("");
  const [file, setFile] = useState(null);
  const [responseMessage, setResponseMessage] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file || !folderID || !type) {
      setResponseMessage("Please fill all fields and select a file.");
      return;
    }

    const formData = new FormData();
    formData.append("folderID", folderID);
    formData.append("type", type);
    formData.append("text", text);
    formData.append("file", file);

    try {
      const response = await axios.post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      setResponseMessage(response.data.message);
    } catch (error) {
      setResponseMessage("Error uploading file: " + error.message);
    }
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Upload Item</h1>
      <form onSubmit={handleSubmit} className="grid gap-4">
        <div>
          <label htmlFor="folderID" className="block mb-1 font-medium">
            Folder ID
          </label>
          <input
            id="folderID"
            type="text"
            value={folderID}
            onChange={(e) => setFolderID(e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label htmlFor="type" className="block mb-1 font-medium">
            Type
          </label>
          <select
            id="type"
            value={type}
            onChange={(e) => setType(e.target.value)}
            className="w-full p-2 border rounded"
          >
            <option value="">Select Type</option>
            <option value="slides">Slides</option>
            <option value="imageWithText">Image with Text</option>
            <option value="singleImage">Single Image</option>
            <option value="videoWithText">Video with Text</option>
            <option value="singleVideo">Single Video</option>
          </select>
        </div>
        <div>
          <label htmlFor="text" className="block mb-1 font-medium">
            Text (optional)
          </label>
          <textarea
            id="text"
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
        <div>
          <label htmlFor="file" className="block mb-1 font-medium">
            File
          </label>
          <input
            id="file"
            type="file"
            onChange={handleFileChange}
            className="w-full"
          />
        </div>
        <button
          type="submit"
          className="p-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Upload
        </button>
      </form>
      {responseMessage && <p className="mt-4 text-green-600">{responseMessage}</p>}
    </div>
  );
};

export default UploadItem;
