import React, { useState, useEffect } from "react";
import { auth } from "./components/firebaseConfig";
import { signInWithEmailAndPassword } from "firebase/auth";
import AddItemForm from "./components/AddItems";
import FileUpload from "./components/FileUpload";
import "./App.css"; // Import the CSS file

const App = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);

  // Scroll to top whenever the component is mounted or user state changes
  useEffect(() => {
    window.scrollTo(0, 0); // Ensure the page scrolls to the top
  }, []); // This will trigger the scroll on initial load

  const handleLogin = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        setUser(userCredential.user); // On successful login, set user state
      })
      .catch((error) => {
        console.error("Login Error: ", error);
        alert("Login failed. Please try again.");
      });
  };

  // When the user logs in, this part of the UI will render
  return user ? (
    <>
    <div className="aad">Welcome admin </div>
    <div className="aad">Welcome admin </div>
    <div className="flex-container">
      <AddItemForm />
      <FileUpload />
    </div>
    </>
  ) : (
    <div className="log">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="inptim"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="inptin"
      />
      <button className="bttn" onClick={handleLogin}>
        Log in
      </button>
    </div>
    
  );
  
};


export default App;
