import React, { useState, useEffect } from "react";
import getItemInfo from "./getItemInfo";

const ItemInfo = ({ folderId }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await getItemInfo(folderId);
        setData(result);
      } catch (err) {
        setError("Failed to fetch folder information.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [folderId]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Folder Information</h1>
      <h2>Folder ID: {data.folderId}</h2>

      <h3>Text Fields</h3>
      <pre>{JSON.stringify(data.textFields, null, 2)}</pre>

      <h3>Images</h3>
      {data.images.length > 0 ? (
        data.images.map((image, index) => (
          <img
            key={index}
            src={`http://localhost:3008${image}`}
            alt={`Image ${index + 1}`}
            style={{ maxWidth: "200px", margin: "10px" }}
          />
        ))
      ) : (
        <p>No images available.</p>
      )}

      <h3>Videos</h3>
      {data.videos.length > 0 ? (
        data.videos.map((video, index) => (
          <video
            key={index}
            controls
            style={{ maxWidth: "400px", margin: "10px" }}
          >
            <source src={`http://localhost:3008${video}`} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        ))
      ) : (
        <p>No videos available.</p>
      )}
    </div>
  );
};

export default ItemInfo;
