import React, { useState } from "react";
import axios from "axios";

function UploadFolder() {
  const [folderId, setFolderId] = useState(Date.now().toString()); // Unique folder ID
  const [files, setFiles] = useState([]);

  const handleFileChange = (e) => {
    setFiles(e.target.files);
  };

  const uploadFiles = async () => {
    const formData = new FormData();

    // Add folder ID to formData
    formData.append("folderId", folderId);

    // Categorize files into images and variant-images
    Array.from(files).forEach((file) => {
      console.log(file.webkitRelativePath); // Log to see the directory structure

      // Check if the file is in the 'images' folder
      if (file.webkitRelativePath.includes("images/")) {
        formData.append("images", file);
      } 
      // Check if the file is in the 'variant-images' folder
      else if (file.webkitRelativePath.includes("images-variants/")) {
        formData.append("images", file);
      }
    });

    try {
      const response = await axios.post("http://localhost:3008/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      console.log("Upload successful:", response.data);
    } catch (error) {
      console.error("Error uploading files:", error);
    }
  };

  return (
    <div>
      <h2>Upload Folder</h2>
      <input type="file" webkitdirectory="true" multiple onChange={handleFileChange} />
      <button onClick={uploadFiles}>Upload</button>
    </div>
  );
}

export default UploadFolder;
