import React, { useState } from "react";

const AddTheme = () => {
  const [themeData, setThemeData] = useState({
    theme: "",
    mode: "default",
    types: ""
  });

  const [imageFile, setImageFile] = useState(null);
  const [message, setMessage] = useState("");

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setThemeData({
      ...themeData,
      [name]: value
    });
  };

  // Handle file input change
  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("theme", themeData.theme);
      formData.append("mode", themeData.mode);
      formData.append("types", themeData.types);
      if (imageFile) {
        formData.append("image", imageFile);
      }

      const response = await fetch("http://localhost:3034/themes", {
        method: "POST",
        body: formData
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(`Theme added successfully: ${data.theme}`);
        setThemeData({ theme: "", mode: "default", types: "" });
        setImageFile(null);
      } else {
        const errorData = await response.json();
        setMessage(`Error: ${errorData.message}`);
      }
    } catch (error) {
      setMessage(`Error: ${error.message}`);
    }
  };

  return (
    <div style={{ marginBottom: "30px" }}>
      <h2>Add a New Theme</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Theme:
          <input
            type="text"
            name="theme"
            value={themeData.theme}
            onChange={handleChange}
            required
          />
        </label>
        <br />
        <label>
          Image:
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            required
          />
        </label>
        <br />
        <label>
          Mode:
          <select name="mode" value={themeData.mode} onChange={handleChange}>
            <option value="default">Default</option>
            <option value="full">Full</option>
          </select>
        </label>
        <br />
        <label>
          Types (comma-separated):
          <input
            type="text"
            name="types"
            value={themeData.types}
            onChange={handleChange}
            placeholder="Type1,Type2,Type3"
          />
        </label>
        <br />
        <button type="submit">Add Theme</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AddTheme;
