import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AddItems.css';

const AddItemForm = () => {
  // Form state variables
  const [category, setCategory] = useState('');
  const [name, setName] = useState('');
  const [usdPrice, setUsdPrice] = useState('');
  const [cryptocurrency, setCryptocurrency] = useState('BNB');
  const [network, setNetwork] = useState('ethereum');
  const [numberOfItems, setNumberOfItems] = useState('');
  const [link, setLink] = useState('');
  const [images, setImages] = useState(['']);
  const [videos, setVideos] = useState(['']);
  const [text, setText] = useState('');

  // Scroll to top when the component is mounted
  useEffect(() => {
    window.scrollTo(0, 0); // Scrolls to the top of the page
  }, []);

  // Handlers for adding more images/videos and managing input state
  const handleAddImage = () => setImages([...images, '']);
  const handleAddVideo = () => setVideos([...videos, '']);
  const handleImageChange = (index, value) => {
    const newImages = [...images];
    newImages[index] = value;
    setImages(newImages);
  };
  const handleVideoChange = (index, value) => {
    const newVideos = [...videos];
    newVideos[index] = value;
    setVideos(newVideos);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const itemData = {
      category,
      item: {
        name,
        usdPrice,
        cryptocurrency,
        network,
        numberOfItems,
        link,
        images,
        videos,
        text,
      },
    };

    try {
      await axios.post('http://localhost:3001/add-item', itemData);
      alert('Item added successfully!');
    } catch (error) {
      console.error('Error adding item:', error);
      alert('Failed to add item.');
    }
  };

  return (
    <form className='formi' onSubmit={handleSubmit}>
        <div className="aad">Welcome admin </div>
      <div className='categ'>
        <label>Category:</label>
        <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} />
      </div>

      <div className='nam'>
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </div>

      <div className='pric'>
        <label>USD Price:</label>
        <input type="number" value={usdPrice} onChange={(e) => setUsdPrice(e.target.value)} />
      </div>

      <div className='cryp'>
        <label>Cryptocurrency:</label>
        <input type="text" value={cryptocurrency} onChange={(e) => setCryptocurrency(e.target.value)} />
      </div>

      <div className='netw'>
        <label>Network:</label>
        <input type="text" value={network} onChange={(e) => setNetwork(e.target.value)} />
      </div>

      <div className='numb'>
        <label>Number of Items:</label>
        <input type="number" value={numberOfItems} onChange={(e) => setNumberOfItems(e.target.value)} />
      </div>

      <div className='lin'>
        <label>Link:</label>
        <input type="text" value={link} onChange={(e) => setLink(e.target.value)} />
      </div>

      <div className='img'>
        <label>Images:</label>
        {images.map((img, index) => (
          <div key={index} className='inp'>
            <input
              type="text"
              placeholder="Image URL"
              value={img}
              onChange={(e) => handleImageChange(index, e.target.value)}
            />
          </div>
        ))}
        <button type="button" className="add-button" onClick={handleAddImage}>Add Another Image</button>
      </div>

      <div className='vid'>
        <label>Videos:</label>
        {videos.map((vid, index) => (
          <div key={index} className='inpt'>
            <input
              type="text"
              placeholder="Video URL"
              value={vid}
              onChange={(e) => handleVideoChange(index, e.target.value)}
            />
          </div>
        ))}
        <button type="button" className="add-button" onClick={handleAddVideo}>Add Another Video</button>
      </div>

      <div className='desc'>
        <label>Description:</label>
        <textarea value={text} onChange={(e) => setText(e.target.value)} />
      </div>

      <button className='bbtno' type="submit">Add Item</button>
    </form>
  );
};

export default AddItemForm;
