// src/components/FileUpload.js
import React, { useState } from "react";
import { storage, auth } from "../firebaseConfig"; // Import the auth and storage
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { onAuthStateChanged } from "firebase/auth";

const FileUpload = ({ setMediaUrl }) => {
  const [file, setFile] = useState(null);
  const [progress, setProgress] = useState(0);

  // Check if the user is authenticated
  const [user, setUser] = useState(null);

  // Listen for authentication state changes
  onAuthStateChanged(auth, (user) => {
    setUser(user);
  });

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = () => {
    if (!user) {
      alert("You need to be logged in to upload files.");
      return;
    }

    const storageRef = ref(storage, "uploads/" + file.name);
    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      "state_changed",
      (snapshot) => {
        const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        setProgress(progress);
      },
      (error) => {
        console.error("Upload error: ", error);
      },
      () => {
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          setMediaUrl(downloadURL); // Return the download URL to the parent component
          alert("File uploaded successfully");
        });
      }
    );
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload</button>
      {progress > 0 && <p>Upload progress: {progress}%</p>}
    </div>
  );
};

export default FileUpload;
