import React, { useState } from 'react';
import axios from 'axios';

function ImageEnhancer() {
  const [image, setImage] = useState(null);
  const [enhancedImage, setEnhancedImage] = useState(null);

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('image', image);

    try {
      const response = await axios.post('http://localhost:3040/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setEnhancedImage(response.data.path);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  return (
    <div>
      <h1>Image Enhancer</h1>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Enhance Image</button>

      {image && <img src={URL.createObjectURL(image)} alt="Original" width="300" />}
      {enhancedImage && (
        <div>
          <h3>Enhanced Image:</h3>
          <img src={`http://localhost:3040/${enhancedImage}`} alt="Enhanced" width="300" />
        </div>
      )}
    </div>
  );
}

export default ImageEnhancer;
