// src/AddItemForm.js
import React, { useState } from 'react';
import axios from 'axios';
import FileUpload from './FileUpload';

const AddItemForm = () => {
  const [category, setCategory] = useState('');
  const [name, setName] = useState('');
  const [usdPrice, setUsdPrice] = useState('');
  const [cryptocurrency, setCryptocurrency] = useState('BNB');
  const [network, setNetwork] = useState('ethereum');
  const [numberOfItems, setNumberOfItems] = useState('');
  const [text, setText] = useState('');
  const [mediaUrls, setMediaUrls] = useState({ images: [], videos: [] });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const itemData = {
      category,
      item: {
        name,
        usdPrice,
        cryptocurrency,
        network,
        numberOfItems,
        images: mediaUrls.images,
        videos: mediaUrls.videos,
        text,
      },
    };

    try {
      await axios.post('http://localhost:3001/add-item', itemData);
      alert('Item added successfully!');
    } catch (error) {
      console.error('Error adding item:', error);
      alert('Failed to add item.');
    }
  };

  const handleImageUpload = (url) => {
    setMediaUrls((prevUrls) => ({
      ...prevUrls,
      images: [...prevUrls.images, url],
    }));
  };

  const handleVideoUpload = (url) => {
    setMediaUrls((prevUrls) => ({
      ...prevUrls,
      videos: [...prevUrls.videos, url],
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      {/* Other input fields */}
      
      <div>
        <label>Upload Images:</label>
        <FileUpload setMediaUrl={handleImageUpload} />
      </div>

      <div>
        <label>Upload Videos:</label>
        <FileUpload setMediaUrl={handleVideoUpload} />
      </div>

      <button type="submit">Add Item</button>
    </form>
  );
};

export default AddItemForm;
