import React, { useState, useEffect } from "react";
import { auth } from "./components/firebaseConfig";
import { signInWithEmailAndPassword } from "firebase/auth";
import AddItemForm from "./components/AddItems";
import FileUpload from "./components/FileUpload";
import UploadFiles from "./components/uploadFolder";
import MissingVariantsChecker from "./components/missingVariants";
import Header from "./components/header";
import "./App.css"; // Import the CSS file
import AddTheme from "./components/AddThemes";
import ImageEnhancer from "./components/cleanImage";
import AddTypeForm from "./components/addTypes";
import AddCategoryForm from "./components/AddCategoryForm";
import RemoveBackground from "./components/removeBG";


const App = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);

  // Scroll to top when the component is rendered or user state changes
  useEffect(() => {
    window.scrollTo(0, 0); // Scroll to the top of the page
  }, [user]); // This will trigger the scroll when the user state is updated

  const handleLogin = () => {
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        setUser(userCredential.user);
      })
      .catch((error) => {
        console.error("Login Error: ", error);
        alert("Login failed. Please try again.");
      });
  };

  return user ?(
    <>
    <div><MissingVariantsChecker/></div>
    <div className="flex-container">
      <div>
        <AddTheme />
      <AddItemForm />
      <Header />
      </div>
      <div>
        <ImageEnhancer/>
        <RemoveBackground />
        <AddCategoryForm />
        <AddTypeForm />
      </div>
      <div>
      <FileUpload />
      <UploadFiles/>
      </div>
    </div>
    </>
  ) : (
    <div className="log">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        className="inptim"
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        className="inptin"
      />
      <button className="bttn" onClick={handleLogin}>Log in</button>
    </div>
    
    
  );
};

export default App;
